class Solution {
public:
    int minimumDistance(vector<vector<int>>& p) {
        int res = INT_MAX, n = p.size();
        multiset<pair<int, int>> a, b;
        for(int i = 0; i < n; i++)
        {
            a.insert({p[i][0] + p[i][1], i});
            b.insert({p[i][0] - p[i][1], i});
        }
        for(int i = 0; i < n; i++)
        {
            int s = p[i][0] + p[i][1], d = p[i][0] - p[i][1];
            a.erase({s, i});
            b.erase({d, i});
            int d1 = a.rbegin()->first - a.begin()->first;
            int d2 = b.rbegin()->first - b.begin()->first;
            res = min(res, max(d1, d2));
            a.insert({s, i});
            b.insert({d, i});
        }
        return res;
    }
};
