class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        int n=points.size();
        vector<int>ban(n);
        int v1=-1,v2=-1,val=-1;
        auto calc=[&](){
            int mx1,mx2,mx3,mx4;
            int p1,p2,p3,p4;
            int f=0;
            for(int i=0;i<n;i++){
                if(ban[i])continue;
                int x=points[i][0];
                int y=points[i][1];
                int d1=x+y;
                int d2=x-y;
                int d3=-x+y;
                int d4=-x-y;
                if(f){
                    int len1=d1+mx4;
                    int len2=d2+mx3;
                    int len3=d3+mx2;
                    int len4=d4+mx1;
                    if(len1>val){
                        val=len1,v1=i,v2=p4;
                    }
                    if(len2>val){
                        val=len2,v1=i,v2=p3;
                    }
                    if(len3>val){
                        val=len3,v1=i,v2=p2;
                    }
                    if(len4>val){
                        val=len4,v1=i,v2=p1;
                    }
                }
                if(!f||d1>mx1)mx1=d1,p1=i;
                if(!f||d2>mx2)mx2=d2,p2=i;
                if(!f||d3>mx3)mx3=d3,p3=i;
                if(!f||d4>mx4)mx4=d4,p4=i;
                f=1;
            }
        };
        calc();
        int s1=v1,s2=v2;
        val=-1;
        ban[s1]=1;
        calc();
        int ans=val;
        val=-1;
        ban[s2]=1,ban[s1]=0;
        calc();
        return min(val,ans);
    }
};
