class Solution:
    def minimumDistance(self, points: List[List[int]]) -> int:
        from sortedcontainers import SortedList as SL
        S, L = SL(), SL()
        for x, y in points:
            S.add(x + y)
            L.add(x - y)
        ans = []
        for x, y in points:
            S.pop(S.bisect_left(x + y))
            L.pop(L.bisect_left(x - y))
            ans.append(max(S[-1] - S[0], L[-1] - L[0]))
            S.add(x + y)
            L.add(x - y)
        return min(ans)
