class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        int n = points.size();
        vector<pair<int, int> > a;
        for (auto &p: points) a.emplace_back(p[0], p[1]);
        auto calc = [&](vector<pair<int, int> > &a, int &p, int &q) {
            int best = -1e9;
            int minz = 1e9, mini;
            int maxz = -1e9, maxi;
            for (int i = 0; i < a.size(); i++) {
                auto [x, y] = a[i];
                int z = x + y;
                if (z < minz) minz = z, mini = i;
                if (z > maxz) maxz = z, maxi = i;
                if (z - minz > best) best = z - minz, p = mini, q = i;
                if (maxz - z > best) best = maxz - z, p = maxi, q = i;
            }
            minz = 1e9, maxz = -1e9;
            for (int i = 0; i < a.size(); i++) {
                auto [x, y] = a[i];
                int z = x - y;
                if (z < minz) minz = z, mini = i;
                if (z > maxz) maxz = z, maxi = i;
                if (z - minz > best) best = z - minz, p = mini, q = i;
                if (maxz - z > best) best = maxz - z, p = maxi, q = i;
            }
            return best;
        };
        int i, j;
        calc(a, i, j);
        auto b = a;
        b.erase(b.begin() + i);
        auto c = a;
        c.erase(c.begin() + j);
        return min(calc(b, i, j), calc(c, i, j));
    }
};
