class Solution {
public:
    int calcs(vector<int> vc) {
        return vc[0] + vc[1];
    }
    int calcd(vector<int> vc) {
        return vc[0] - vc[1];
    }
    int fun(vector<vector<int>> &points, int ind) {
        int indmns = 0, indmxs = 0, indmxd = 0, indmnd = 0;
        if(ind == 0) {
            indmns = 1;
            indmxs = 1;
            indmxd = 1;
            indmnd = 1;
        }
        int n = points.size();
        for(int i = 1; i < n; i++) {
            if(i == ind)
                continue;
            if(calcs(points[i]) > calcs(points[indmxs]))
                indmxs = i;
            if(calcs(points[i]) < calcs(points[indmns]))
                indmns = i;
            if(calcd(points[i]) > calcd(points[indmxd]))
                indmxd = i;
            if(calcd(points[i]) < calcd(points[indmnd]))
                indmnd = i;
        }
        return max(calcs(points[indmxs]) - calcs(points[indmns]), calcd(points[indmxd]) - calcd(points[indmnd]));
    }
    int minimumDistance(vector<vector<int>>& points) {
        int indmns = 0, indmxs = 0, indmxd = 0, indmnd = 0;
        int n = points.size();
        for(int i = 1; i < n; i++) {
            if(calcs(points[i]) > calcs(points[indmxs]))
                indmxs = i;
            if(calcs(points[i]) < calcs(points[indmns]))
                indmns = i;
            if(calcd(points[i]) > calcd(points[indmxd]))
                indmxd = i;
            if(calcd(points[i]) < calcd(points[indmnd]))
                indmnd = i;
        }
        return min({fun(points, indmns), fun(points, indmxs), fun(points, indmnd), fun(points, indmxd)});
    }
};
