class Solution {
public:
    void calc(vector<vector<int>> &a, int &bi, int &r){
        int i, j, k, x, y, n = a.size(), zd[4][2], v, fv, now;
        r = -1;
        for (i = 0; i < 4; ++i) zd[i][0] = zd[i][1] = -1000000000;
        // for (i = 0; i < n; ++i) cout<<a[i][0]<<" "<<a[i][1]<<endl;
        for (i = 0; i < n; ++i){
            j = 0;
            for (x = -1; x <= 1; x += 2)
                for (y = -1; y <= 1; y += 2){
                    v = a[i][0] * (-x) + a[i][1] * (-y);
                    if (v > zd[j][0]){
                        zd[j][1] = zd[j][0];
                        zd[j][0] = v;
                    }
                    else{
                        if (v > zd[j][1]) zd[j][1] = v;
                    }
                    ++j;
                }
        }

        for (i = 0; i < n; ++i){
            j = 0;
            for (x = -1; x <= 1; x += 2)
                for (y = -1; y <= 1; y += 2){
                    v = a[i][0] * x + a[i][1] * y;
                    fv = a[i][0] * (-x) + a[i][1] * (-y);
                    if (zd[j][0] == fv) now = v + zd[j][1];
                    else now = v + zd[j][0];
                    if (now > r){
                        r = now;
                        bi = i;
                    }

                    ++j;
                }
        }
    }

    int minimumDistance(vector<vector<int>>& yuan) {
        int n = yuan.size(), i, j, k, res = 1000000000, v, q;
        calc(yuan, i, v);
        for (j = 0; j < n; ++j)
            if (j != i && labs(yuan[i][0] - yuan[j][0]) + labs(yuan[i][1] - yuan[j][1]) == v)
                break;

        res = v;
        vector<vector<int>> a;
        a.clear();
        for (k = 0; k < n; ++k)
            if (k != i)
                a.push_back(yuan[k]);
        calc(a, q, v);
        res = min(res, v);

        a.clear();
        for (k = 0; k < n; ++k)
            if (k != j)
                a.push_back(yuan[k]);
        calc(a, q, v);
        res = min(res, v);

        return res;
    }
};
