class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        int n = points.size();
		vector<pair<int,int>>a(n), b(n);
		unordered_map<int, int>p1, p2;
		for (int i = 0; i < n; i++) {
			a[i] = { points[i][0] + points[i][1], i };
			b[i] = { points[i][0] - points[i][1], i };
		}
		sort(a.begin(), a.end());
        sort(b.begin(), b.end());
		for (int i = 0; i < n; i++)p1[a[i].second] = i, p2[b[i].second] = i;
		//cout << a.back().first - a.front().first << " " << b.back().first - b.front().first << "\n";
		int ret = 2e9;
		for (int i = 0; i < n; i++) {
			int x = p1[i], y = p2[i];
			int X, Y;
			if (x != 0 && x != n - 1)X = a.back().first - a.front().first;
			else if (x == 0)X = a.back().first - a[1].first;
			else X = a[n - 2].first - a.front().first;

			if (y != 0 && y != n - 1)Y = b.back().first - b.front().first;
			else if (y == 0)Y = b.back().first - b[1].first;
			else Y = b[n - 2].first - b.front().first;
			ret = min(ret, max(X, Y));
		}
		return ret;
    }
};
