class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
		int n = points.size();
		vector<int> inter;
		for (int dx = -1; dx <= 1; dx += 2) { 
			for (int dy = -1; dy <= 1; dy += 2) {
				int mxi = 0;
				for (int i = 0; i < n; ++i) {
					int val = dx * points[i][0] + dy * points[i][1];
					int mval = dx * points[mxi][0] + dy * points[mxi][1];
					if (val > mval) {
						mxi = i;
					}
				}
				inter.push_back(mxi);
			}
		}
		for (int i = 0; i < min(n, 3); ++i){
			inter.push_back(i);
		}
		int ansich = 1e9;
		for (int i : inter) {
			vector<int> inter1;
			for (int dx = -1; dx <= 1; dx += 2) { 
				for (int dy = -1; dy <= 1; dy += 2) {
					int mxi = 0;
					if (i == 0) mxi = 1;
					for (int j = 0; j < n; ++j) {
						if (j == i) continue;
						int val = dx * points[j][0] + dy * points[j][1];
						int mval = dx * points[mxi][0] + dy * points[mxi][1];
						if (val > mval) {
							mxi = j;
						}
					}
					inter1.push_back(mxi);
				}
			}
			for (int j = 0; j < min(n, 3); ++j) {
				if (j != i) {
					inter1.push_back(j);
				}
			}
			int mxd = 0;
			for (int x : inter1) 
				for (int y : inter1) {
					if (x != y) {
						mxd = max(mxd, abs(points[x][0] - points[y][0]) + abs(points[x][1] - points[y][1]));
					}
				}
			ansich = min(ansich, mxd);
		}
		return ansich;
    }
};
