#define ll long long int
class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        ll ans=LLONG_MAX;
        vector<ll>xpy;
        vector<ll>xmy;
        int n=points.size();
        for(auto it:points)
        {
            xpy.push_back(it[0]+it[1]);
            xmy.push_back(it[0]-it[1]);
        }
        sort(xpy.begin(),xpy.end());
        sort(xmy.begin(),xmy.end());
        for(int i=0;i<n;i++)
        {
            ll cxpy=points[i][0]+points[i][1];
            ll cxmy=points[i][0]-points[i][1];
            ll mxxpy=(xpy[n-1]==cxpy)?xpy[n-2]:xpy[n-1];
            ll mnxpy=(xpy[0]==cxpy)?xpy[1]:xpy[0];
            ll mxxmy=(xmy[n-1]==cxmy)?xmy[n-2]:xmy[n-1];
            ll mnxmy=(xmy[0]==cxmy)?xmy[1]:xmy[0];
            ll val=max((mxxpy-mnxpy),(mxxmy-mnxmy));
            ans=min(ans,val);
        }
        return ans;

    }
};
