class Solution(object):
    def minimumDistance(self, points):
        """
        :type points: List[List[int]]
        :rtype: int
        """
        s=sorted([(p[0]+p[1],i) for i,p in enumerate(points)])
        d=sorted([(p[0]-p[1],i) for i,p in enumerate(points)])

        def check(k):
            us=s[-1][0] if s[-1][1]!=k else s[-2][0]
            vs=s[0][0] if s[0][1]!=k else s[1][0]
            ud=d[-1][0] if d[-1][1]!=k else d[-2][0]
            vd=d[0][0] if d[0][1]!=k else d[1][0]
            return max(us-vs,ud-vd)

        return min(check(s[0][1]),check(s[-1][1]),check(d[0][1]),check(d[-1][1]))
