class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        multiset<int> a, b;
        int n = points.size();
        for (int i=0; i<n; i++) {
            a.insert(points[i][0] + points[i][1]);
            b.insert(points[i][0] - points[i][1]);
        }
        int res = INT_MAX;
        for (int i=0; i<n; i++) {
            a.erase(a.find(points[i][0] + points[i][1]));
            b.erase(b.find(points[i][0] - points[i][1]));
            res = min(res, max(*a.rbegin() - *a.begin(), *b.rbegin() - *b.begin()));
            a.insert(points[i][0] + points[i][1]);
            b.insert(points[i][0] - points[i][1]);
        }
        return res;
    }
};
