class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        int n = points.size();
        vector<vector<int>>sum, dif;
        int ind = 0;
        for(auto i:points){
            sum.push_back({i[0]+i[1], ind});
            dif.push_back({i[0]-i[1], ind});
            ind++;
        }
        sort(sum.begin(), sum.end());
        sort(dif.begin(), dif.end());
        int mx = INT_MAX;
        for(int i=0;i<n;i++){
            int l=0,h=n-1;
            if(sum[l][1] == i){
                l++;
            }
            if(sum[h][1] == i){
                h--;
            }
            int val = sum[h][0] - sum[l][0];
            l=0,h=n-1;
            if(dif[l][1] == i){
                l++;
            }
            if(dif[h][1] == i){
                h--;
            }
            val = max(val, dif[h][0] - dif[l][0]);
            mx = min(mx, val);
        }
        return  mx;
    }
};
