        s.in(v[i][j].S);
      }
      v[i].erase(remove(all(v[i]),P(-1,-1)),v[i].end());
      v[i].erase(unique(all(v[i])),v[i].end());
      reverse(all(v[i]));
      while(v[i].size()>2) v[i].pop_back();
    }
