int dis(int x1,int y1,int x2,int y2)
{
    return abs(x1-x2)+abs(y1-y2);
}

pair<int,int> doit(vector<vector<int> >& a)
{
    int x,y,n,i,misum,masum,midiff,madiff,sum,diff,ma,madis,vdis;
