    misum=(1<<30);
    masum=-(1<<30);
    midiff=(1<<30);
    madiff=-(1<<30);
    for (i=0;i<n;i++)
    {
        sum=a[i][0]+a[i][1];
        diff=a[i][0]-a[i][1];
        misum=min(misum,sum);
        masum=max(masum,sum);
        midiff=min(midiff,diff);
        madiff=max(madiff,diff);
    }
    ret=make_pair(-1,-1);
    ma=-1;
    for (i=0;i<n;i++)
    {
        sum=a[i][0]+a[i][1];
        diff=a[i][0]-a[i][1];
        madis=max(max(sum-misum,masum-sum),max(diff-midiff,madiff-diff));
