class Solution:
    def minimumDistance(self, points: List[List[int]]) -> int:
        a=[]
        b=[]
        for i,(x,y) in enumerate(points):
            a.append([x+y,i])
            b.append([x-y,i])
        a.sort()
        b.sort()
        def get1(a,b):
            ans1=a[-1][0]-a[1][0]
            if b[-1][1]==a[0][1]:
                ans2=b[-2][0]-b[0][0]
            elif b[0][1]==a[0][1]:
                ans2=b[-1][0]-b[1][0]
            else:
                ans2=b[-1][0]-b[0][0]
            return max(ans1,ans2)
        def get2(a,b):
            ans1=a[-2][0]-a[0][0]
            if b[-1][1]==a[-1][1]:
                ans2=b[-2][0]-b[0][0]
            elif b[0][1]==a[-1][1]:
                ans2=b[-1][0]-b[1][0]
            else:
                ans2=b[-1][0]-b[0][0]
            return max(ans1,ans2)

        a1,a2,a3,a4=get1(a,b),get2(a,b),get1(b,a),get2(b,a)
        return min(a1,a2,a3,a4)
