using namespace std;  
using ll = long long;
#define FOR(i, a, b) for (int i = a; i < b; ++i)
#define ve vector


class Solution {
public:
    int minimumDistance(vector<vector<int>>& a) {
        sort(a.begin(), a.end());
        vector<pair<int, int>> p;
        for(int i = 0; i < a.size(); i++) {
            int x = a[i][0], y = a[i][1];
            p.push_back({x, y});
        }


        int ans = 1e9;
        int n = p.size();
        vector<pair<int, int>> c, d;
        for(int i = 0; i < n; i++) {
            int v1 = p[i].first + p[i].second;
            int v2 = p[i].first - p[i].second;
            c.push_back({v1, i});
            d.push_back({v2, i});
        }



        sort(c.begin(), c.end());
        sort(d.begin(), d.end());

        /*for(int i = 0; i < n; i++) {
            cout << c[i].first << " " << c[i].second << endl; 
        }
        cout << endl;
        for(int i = 0; i < n; i++) {
            cout << d[i].first << " " << d[i].second << endl; 
        }*/

        for(int i = 0; i < n; i++) {
            //delete this point
            int v1 = -1, v2 = -1;
            int idx = c[i].second;
            if(i == 0) v1 = c.back().first - c[1].first;
            else if(i == n - 1) v1 = c[n - 2].first - c[0].first;
            else v1 = c.back().first - c[0].first;

            if(idx == d[0].second) v2 = d.back().first - d[1].first;
            else if(idx == d[n - 1].second) v2 = d[n - 2].first - d[0].first;
            else v2 = d.back().first - d[0].first;

            ans = min(ans, max(v1, v2));
            //if(ans == 10) cout << "gg " << i <<" " << v1 << " " << v2 << endl;

        }


        return ans;
    }
};

