class Solution {
public:


    using ll=long long;
    int minimumDistance(vector<vector<int>>& p) {
            multiset<pair<ll,ll>> a,b;
            ll n=p.size();
            for(ll i=0;i<n;i++){
                a.insert({p[i][0]+p[i][1],i});
                b.insert({p[i][0]-p[i][1],i});
            }
            ll ans=1e18;
            for(ll i=0;i<n;i++){
                ll x=p[i][0]+p[i][1];
                ll y=p[i][0]-p[i][1];
                a.erase(make_pair(x,i));
                b.erase(make_pair(y,i));

                ll cur=max(a.rbegin()->first - a.begin()->first,b.rbegin()->first-b.begin()->first);
                ans=min(ans,cur);

                a.insert({x,i});
                b.insert({y,i});
            }

        return ans;
    }
};
