class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {

        int n = points.size();
        map<int,int> st,st1;


       for(int i=0;i<n;i++){
           st[points[i][0]+points[i][1]]++;
           st1[points[i][0]-points[i][1]]++;
       }
        int ans = INT_MAX;
        for(int i=0;i<n;i++){
            st[points[i][0]+points[i][1]]--;
            st1[points[i][0]-points[i][1]]--;
            if(st[points[i][0]+points[i][1]]==0)
                st.erase(points[i][0]+points[i][1]);

            if(st1[points[i][0]-points[i][1]] == 0)
                st1.erase(points[i][0]-points[i][1]);

            if(st.size() and st1.size())
                ans = min(ans,max(st.rbegin()->first-st.begin()->first,st1.rbegin()->first-st1.begin()->first));
            else
                ans = 0;

            st[points[i][0]+points[i][1]]++;
            st1[points[i][0]-points[i][1]]++;
        }
        return ans;

    }
};
