class Solution:
    def minimumDistance(self, points: List[List[int]]) -> int:
        res1, res2 = [points[i][0] + points[i][1] for i in range(len(points))], [points[i][0] - points[i][1] for i in range(len(points))]
        res1.sort()
        res2.sort()
        max1, cnt1 = res1[-1], 0
        for point in points:
            if max1 == point[0] + point[1]:
                cnt1 += 1
        max2, cnt2 = res2[-1], 0
        for point in points:
            if max2 == point[0] - point[1]:
                cnt2 += 1
        min1, cnt3 = res1[0], 0
        for point in points:
            if min1 == point[0] + point[1]:
                cnt3 += 1
        min2, cnt4 = res2[0], 0
        for point in points:
            if min2 == point[0] - point[1]:
                cnt4 += 1
        ans = max(max1 - min1, max2 - min2)
        for i in range(len(points)):
            x1, x2, x3, x4 = max1, max2, min1, min2
            if points[i][0] + points[i][1] == max1:
                if cnt1 == 1:
                    x1 = res1[-2]
            if points[i][0] + points[i][1] == min1:
                if cnt3 == 1:
                    x3 = res1[1]
            if points[i][0] - points[i][1] == max2:
                if cnt2 == 1:
                    x2 = res2[-2]
            if points[i][0] - points[i][1] == min2:
                if cnt4 == 1:
                    x4 = res2[1]
            ans = min(ans, max(x1 - x3, x2 - x4))
        return ans
