class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        ranges::sort(points);
        multiset<int> ss, ds;
        for (auto& p : points) {
            int x = p[0], y = p[1];
            ss.insert(x + y);
            ds.insert(x - y);
        }
        int m = INT_MAX;
        for (auto& p : points) {
            int x = p[0], y = p[1];
            ss.extract(x + y);
            ds.extract(x - y);
            int c = max((*ss.rbegin()) - (*ss.begin()), (*ds.rbegin()) - (*ds.begin()));
            m = min(m, c);
            ss.insert(x + y), ds.insert(x - y);
        }
        return m;
    }
};
