class Solution {
public:

    set<int>s[4];
    map<int,int>m[4];
    void add(int x, int idx){
        s[idx].insert(x);
        m[idx][x]++;
    }
    void del(int x, int idx){
        if(--m[idx][x] == 0){
            m[idx].erase(x);
            s[idx].erase(x);
        }
    }
    int getmin(int idx){
        return *s[idx].begin();
    }
    int getmax(int idx){
        return *(--s[idx].end());
    }
    int minimumDistance(vector<vector<int>>& points) {
        for(int i = 0; i < 4; i++)s[i].clear(), m[i].clear();
        vector<vector<int>>a(4, vector<int>(points.size(), 0));
        for(int i = 0; i < points.size(); i++){
            a[0][i] = points[i][0] + points[i][1];
            a[1][i] = points[i][0] - points[i][1];
            a[2][i] = - points[i][0] + points[i][1];
            a[3][i] = - points[i][0] - points[i][1];
        }
        for(int i = 0; i < 4; i++){
            for(int j = 0; j < points.size(); j++)add(a[i][j], i);
        }
        int ans = INT_MAX;
        for(int i = 0; i < points.size(); i++){
            del(a[0][i], 0);del(a[1][i], 1);del(a[2][i], 2);del(a[3][i], 3);
            int nowans = getmax(0) - getmin(0);
            for(int j = 1; j <= 3; j++)nowans = max(nowans, getmax(j) - getmin(j));
            ans = min(ans, nowans);
            add(a[0][i], 0);add(a[1][i], 1);add(a[2][i], 2);add(a[3][i], 3);
        }
        return ans;
    }
};
