class Solution {
public:
    int minimumDistance(vector<vector<int>>& a) {
        multiset<int>st;
        multiset<int>st2;
        int n=a.size();
        for (int i=0;i<n;i++){
            st.insert(a[i][0]+a[i][1]);
            st2.insert(a[i][0]-a[i][1]);
        }
        int ans=INT_MAX;
        for (int i=0;i<n;i++){
            st.erase(st.find(a[i][0]+a[i][1]));
            st2.erase(st2.find(a[i][0]-a[i][1]));
            int sum=max(*(prev(st.end()))-*(st.begin()),*(prev(st2.end()))-*(st2.begin()));
            ans=min(ans,sum);
            st.insert(a[i][0]+a[i][1]);
            st2.insert(a[i][0]-a[i][1]);
        }
        return ans;

    }
};
