class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
            multiset<int> a,b;
            for(auto &i:points){
                    a.insert(i[0]+i[1]);
                    b.insert(i[0]-i[1]);
            }
            int n=points.size();
            int res=INT_MAX;
            for(auto &i:points){
                    a.erase(a.find(i[0]+i[1]));
                    b.erase(b.find(i[0]-i[1]));
                    res=min(res,max(*a.rbegin()-*a.begin(),*b.rbegin()-*b.begin()));
                    a.insert(i[0]+i[1]);
                    b.insert(i[0]-i[1]);

            }
            return res;
    }
};
