class Solution {
public:
    int mn(vector<vector<int>>& points,int md){
      vector<vector<pair<int,int>>> v(4,vector<pair<int,int>>());
      for(int i=0;i<points.size();i++){
        if(i==md){
          continue;
        }
        v[0].push_back({points[i][0]+points[i][1],i});
        v[1].push_back({-points[i][0]+points[i][1],i});
        v[2].push_back({points[i][0]-points[i][1],i});
        v[3].push_back({-points[i][0]-points[i][1],i});
      }
      for(int i=0;i<4;i++){
        sort(v[i].begin(),v[i].end());
      }
      int n=points.size();
      int mx=-1;
      for(int i=0;i<4;i++){
        mx=max(mx,v[i][n-2].first-v[i][0].first);
      }
      return mx;
    }
    int minimumDistance(vector<vector<int>>& points) {
        vector<vector<pair<int,int>>> v(4,vector<pair<int,int>>());
        for(int i=0;i<points.size();i++){
          v[0].push_back({points[i][0]+points[i][1],i});
          v[1].push_back({-points[i][0]+points[i][1],i});
          v[2].push_back({points[i][0]-points[i][1],i});
          v[3].push_back({-points[i][0]-points[i][1],i});
        }

        int n=points.size();
        for(int i=0;i<4;i++){
          sort(v[i].begin(),v[i].end());
        }
        vector<vector<pair<int,int>>> vv(4,vector<pair<int,int>>());
        int mx=-1;
        vector<int> vm;
        for(int i=0;i<4;i++){
          if(v[i][n-1].first-v[i][0].first>mx){
            mx=v[i][n-1].first-v[i][0].first;
            vm={v[i][n-1].second,v[i][0].second};
          }else if(v[i][n-1].first-v[i][0].first==mx){
            vm.push_back(v[i][n-1].second);
            vm.push_back(v[i][0].second);
          }
        }
        int ans=INT_MAX;
        for(int i=0;i<vm.size();i++){
          ans=min(mn(points,vm[i]),ans);
        }
        return ans;
    }
};
