class Solution {
public:

    int minimumDistance(vector<vector<int>>& points) {
        multiset<int>sum,diff;
        for(int i=0;i<points.size();i++)
        {
            sum.insert(points[i][0]+points[i][1]);
            diff.insert(points[i][0]-points[i][1]);
        }
        int ans=INT_MAX;
        for(int i=0;i<points.size();i++)
        {
            int sum_contri=points[i][0]+points[i][1];
            int diff_contri=points[i][0]-points[i][1];
            sum.erase(sum.find(sum_contri));
            diff.erase(diff.find(diff_contri));
            ans=min(ans,max(*sum.rbegin()-*sum.begin(),*diff.rbegin()-*diff.begin()));
            sum.insert(sum_contri);
            diff.insert(diff_contri);
        }
        return ans;
    }
};
