class Solution {
public:
	int minimumDistance(vector<vector<int>>& p) {
		multiset<int> Hx, Hy;
		for (auto& e : p)
		{
			int x = e[0] + e[1], y = e[0] - e[1];
			e[0] = x, e[1] = y;
			Hx.insert(e[0]);
			Hy.insert(e[1]);
		}
		int res = 1000000000;
		for (auto& e : p)
		{
			int x = e[0], y = e[1];
			Hx.erase(Hx.find(x));
			Hy.erase(Hy.find(y));
			int tx = *Hx.rbegin() - *Hx.begin();
			int ty = *Hy.rbegin() - *Hy.begin();
			res = min(res, max(tx, ty));
			Hx.insert(x);
			Hy.insert(y);
		}
		return res;
	}
};
