class Solution {
public:
    static bool cmp(pair<int, int> a, pair<int, int> b){
        return a.first < b.first;
    }
    int minimumDistance(vector<vector<int>>& points) {
        int n = points.size();
        vector<pair<int, int>> xysum, xysub;
        for(int i = 0; i < n; i++){
            int a = points[i][0] + points[i][1], b = points[i][0] - points[i][1];
            xysum.push_back({a, i});
            xysub.push_back({b, i});
        }
        sort(xysum.begin(), xysum.end(), cmp);
        sort(xysub.begin(), xysub.end(), cmp);
        // for(int i = 0; i < n; i++){
        //     printf("%d %d\n", xysum[i].first, xysum[i].second);
        // }
        int ans = -1;
        for(int i = 0; i < 4; i++){
            int now;
            if(i == 0){
                now = xysum[0].second;
            }
            else if(i == 1){
                now = xysum[n - 1].second;
            }
            else if(i == 2){
                now = xysub[0].second;
            }
            else if(i == 3){
                now = xysub[n - 1].second;
            }
            int a, b, c, d;
            a = now == xysum[n - 1].second ? xysum[n - 2].first : xysum[n - 1].first;
            b = now == xysum[0].second ? xysum[1].first : xysum[0].first;
            c = now == xysub[n - 1].second ? xysub[n - 2].first : xysub[n - 1].first;
            d = now == xysub[0].second ? xysub[1].first : xysub[0].first;
            ans = ans == -1 ? max(a - b, c - d) : min(ans, max(a - b, c - d));
        }
        return ans;
    }
};
