class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        multiset <int> dx;
        multiset <int> dy;
        for(auto &p:points){
            int x = p[0] , y = p[1];
            dx.insert(x+y);
            dy.insert(x-y);
        }
        int ans  = 1e9;
        for(auto &p:points){
            int x = p[0] , y = p[1];
            int s1 = x+ y;
            int s2 = x - y;
            dx.erase(dx.find(s1));
            dy.erase(dy.find(s2));
            ans = min(ans,max(*dx.rbegin()-*dx.begin(),*dy.rbegin()-*dy.begin()));
            dx.insert(s1);
            dy.insert(s2);
        }
        return ans;

    }
};
