class Solution 
{
public:
    int minimumDistance(vector<vector<int>>& nums) 
    {
        int n = nums.size();
        int id1 = -1, id2 = -1, id3 = -1, id4 = -1;
        int maxS = -2e9, minS = 2e9, maxD = -2e9, minD = 2e9;
        for(int i = 0; i < n; i ++)
        {
            if(nums[i][0] + nums[i][1] > maxS)
            {
                maxS = nums[i][0] + nums[i][1];
                id1 = i;
            }
            if(nums[i][0] + nums[i][1] < minS)
            {
                minS = nums[i][0] + nums[i][1];
                id2 = i;
            }
            if(nums[i][0] - nums[i][1] > maxD)
            {
                maxD = nums[i][0] - nums[i][1];
                id3 = i;
            }
            if(nums[i][0] - nums[i][1] < minD)
            {
                minD = nums[i][0] - nums[i][1];
                id4 = i;
            }
        }
        if(maxS-minS < maxD-minD)
            id1 = id3, id2 = id4;
        //id1 and id2 are indices of ones to remove
        int i1 = id1, i2 = id2;
        int ans = 2e9;
        for(int j = 0; j < 2; j ++)
        {
            id1 = -1, id2 = -1, id3 = -1, id4 = -1;
            maxS = -2e9, minS = 2e9, maxD = -2e9, minD = 2e9;
            for(int i = 0; i < n; i ++)
            {
                if((i == i1 && j == 0) || (i == i2 && j == 1)) continue;
                if(nums[i][0] + nums[i][1] > maxS)
                {
                    maxS = nums[i][0] + nums[i][1];
                    id1 = i;
                }
                if(nums[i][0] + nums[i][1] < minS)
                {
                    minS = nums[i][0] + nums[i][1];
                    id2 = i;
                }
                if(nums[i][0] - nums[i][1] > maxD)
                {
                    maxD = nums[i][0] - nums[i][1];
                    id3 = i;
                }
                if(nums[i][0] - nums[i][1] < minD)
                {
                    minD = nums[i][0] - nums[i][1];
                    id4 = i;
                }
            }
            ans = min(ans, max(maxS-minS, maxD-minD));
        }
        return ans;
    }
};
