class Solution {
public:

    int minimumDistance(vector<vector<int>>& points) {
        vector<int> v,v1;
        for(auto x: points){
            v.push_back(x[0]+x[1]);
            v1.push_back(x[0]-x[1]);
        }
        sort(v.begin(),v.end());
        sort(v1.begin(),v1.end());
        //max=(v.back()-v.front(),v1.back()-v1.front());
        int res=1e9;
        for(auto x: points){
            int ans=-1e9;
            int a=x[0]+x[1];
            int b=x[0]-x[1];
            int d,e,f,g;
            d=v.back();
            e=v.front();
            f=v1.back();
            g=v1.front();
            if(d==a){
                d=v[v.size()-2];
            }
            else if(e==a){
                e=v[1];
            }
            if(f==b){
                f=v1[v1.size()-2];
            }
            else if(g==b){
                g=v1[1];

            }
            ans=max(d-e,f-g);
            res=min(res,ans);

        }
        return res;


    }
};
