class Solution {
public:
    int minimumDistance(vector<vector<int>>& A) {
        int N=A.size();
        int minsum, maxsum, mindiff, maxdiff;

        minsum = maxsum = A[0][0] + A[0][1];
        mindiff = maxdiff = A[0][0] - A[0][1];
        int ind1=0,ind2=0,temp1=0,temp2=0,temp3=0,temp4=0;
        for (int i = 1; i < N; i++) {
            int sum = A[i][0] + A[i][1];
            int diff = A[i][0] - A[i][1];
            if (sum < minsum){
                minsum = sum;
                temp1=i;
            }
            else if (sum > maxsum){
                maxsum = sum;
                temp2=i;
            }
            if (diff < mindiff){
                mindiff = diff;
            temp3=i;
            }
            else if (diff > maxdiff){
                maxdiff = diff;
                temp4=i;
            }
        }
        if(maxsum-minsum>maxdiff-mindiff){
            ind1=temp1;
            ind2=temp2;
        }
        else{
            ind1=temp3;
            ind2=temp4;
        }

        // cout<<ind1<<" "<<ind2<<endl;
        int maximum = max(maxsum - minsum, maxdiff - mindiff);
        vector<vector<int>>A2=A;
        vector<vector<int>>A3=A;
        A2.erase(A2.begin()+ind1);
        A3.erase(A3.begin()+ind2);


        minsum = maxsum = A2[0][0] + A2[0][1];
        mindiff = maxdiff = A2[0][0] - A2[0][1];
        for (int i = 1; i < N-1; i++) {
            int sum = A2[i][0] + A2[i][1];
            int diff = A2[i][0] - A2[i][1];
            if (sum < minsum)
                minsum = sum;
            else if (sum > maxsum)
                maxsum = sum;
            if (diff < mindiff)
                mindiff = diff;
            else if (diff > maxdiff)
                maxdiff = diff;
        }

        int maximum2 = max(maxsum - minsum, maxdiff - mindiff);

        minsum = maxsum = A3[0][0] + A3[0][1];
        mindiff = maxdiff = A3[0][0] - A3[0][1];
        for (int i = 1; i < N-1; i++) {
            int sum = A3[i][0] + A3[i][1];
            int diff = A3[i][0] - A3[i][1];
            if (sum < minsum)
                minsum = sum;
            else if (sum > maxsum)
                maxsum = sum;
            if (diff < mindiff)
                mindiff = diff;
            else if (diff > maxdiff)
                maxdiff = diff;
        }

        int maximum3 = max(maxsum - minsum, maxdiff - mindiff);
        return min(maximum,min(maximum2,maximum3));
    }
};
