#define ll long long
class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        multiset<ll>ms,me;
        int n=points.size();
        for(int i=0;i<n;++i){
            ms.insert(points[i][0]+points[i][1]);
            me.insert(points[i][0]-points[i][1]);
        }
        ll mn=1e18;
        for(int i=0;i<n;i++){
            ll vs=points[i][0]+points[i][1];
            ll ve=points[i][0]-points[i][1];
            ms.erase(ms.find(vs));
            me.erase(me.find(ve));
            ll f=*ms.rbegin();
            ll s=*ms.begin();
            ll ff=*me.rbegin();
            ll ss=*me.begin();
            ll val=max(f-s,ff-ss);
            ms.insert(vs);
            me.insert(ve);
            mn=min(mn,val);
        }
        return mn;
    }
};
