#include<bits/stdc++.h>

using namespace std;

#define  len(x) ((int)((x).size()))
#define PQ priority_queue
#define all(x) (x).begin(),(x).end()

using ll = long long;
using ld = long double;

const array<int, 8> dx{0, -1, 0, 1, 1, 1, -1, -1};
const array<int, 8> dy{1, 0, -1, 0, 1, -1, 1, -1};

mt19937_64 mrand(random_device{}());

class Solution {
public:
    int minimumDistance(vector<vector<int>>& b) {
        ll left = 0;
        ll right = 1e15;

        int N = len(b);
        vector<pair<int, int>> a;
        for (int i = 0; i < N; i++)
            a.push_back({b[i][0], b[i][1]});
        sort(all(a));

        auto get = [&](int x)->ll{
            array<ll, 4> dp{};
            if (x) {
                dp[0] = a[0].first + a[0].second;
                dp[1] = a[0].first - a[0].second;
                dp[2] = -a[0].first + a[0].second;
                dp[3] = -a[0].first - a[0].second;
            } else{
                dp[0] = a[1].first + a[1].second;
                dp[1] = a[1].first - a[1].second;
                dp[2] = -a[1].first + a[1].second;
                dp[3] = -a[1].first - a[1].second;
            }
            ll res = 0;
            for (int i = 0; i < N; i++) {
                if (i == x)
                    continue;
                auto [x, y] = a[i];
                ll ans = -x - y + dp[0];
                ans = max(ans, -x + y + dp[1]);
                ans = max(ans, x - y + dp[2]);
                ans = max(ans, x + y + dp[3]);
                res = max(res, ans);
                dp[0] = max(dp[0], 1LL * x + y);
                dp[1] = max(dp[1], 1LL * x - y);
                dp[2] = max(dp[2], -1LL * x + y);
                dp[3] = max(dp[3], -1LL * x - y);
            }
            return res;
        };
        const ll inf = 1e18;
        ll ans = inf;
        for (int i = 0; i < min(N, 100); i++) {
            ans = min(ans, get(i));
        }
        reverse(all(a));
        for (int i = 0; i < min(N, 100); i++) {
            ans = min(ans, get(i));
        }
        for (int i = 0; i < N; i++) {
            swap(a[i].first, a[i].second);
        }
        sort(all(a));
        for (int i = 0; i < min(N, 100); i++) {
            ans = min(ans, get(i));
        }
        reverse(all(a));
        for (int i = 0; i < min(N, 100); i++) {
            ans = min(ans, get(i));
        }
        return (int)(ans);
    }
};
