    // 找出最大距离的两点 移除其中一个

    int minimumDistance(vector<vector<int>>& points) {
        int n=points.size();
        // sort(points.begin(),points.end());
        // this->p=points;
        int a=-1,b=-1;
        int mxd=-1;
        for(int i=0;i<4;i++){
            map<int,int> mp;
            for(int j=0;j<n;j++){
                int x=points[j][0],y=points[j][1];
                int d;
                if(i==0) d=x+y;
                else if(i==1) d=x-y;
                else if(i==2) d=-x+y;
                else d=-x-y;
                mp[d]=j;
            }
            int v1=mp.begin()->first,v2=mp.rbegin()->first;
            if(v2-v1>mxd){
                mxd=v2-v1;
                a=mp.begin()->second,b=mp.rbegin()->second;
            }
        }
        // 删除a或者删除b
        vector<vector<int>> p1,p2;
        p1=points,p2=points;
        p1.erase(p1.begin()+a);
        p2.erase(p2.begin()+b);

        int mx1=-1,mx2=-1;
        for(int i=0;i<4;i++){
            map<int,int> mp;
            for(int j=0;j<n-1;j++){
                int x=p1[j][0],y=p1[j][1];
                int d;
                if(i==0) d=x+y;
                else if(i==1) d=x-y;
                else if(i==2) d=-x+y;
                else d=-x-y;
                mp[d]=j;
            }
            int v1=mp.begin()->first,v2=mp.rbegin()->first;
            if(v2-v1>mx1){
                mx1=v2-v1;
            }
        }
        for(int i=0;i<4;i++){
            map<int,int> mp;
            for(int j=0;j<n-1;j++){
                int x=p2[j][0],y=p2[j][1];
                int d;
                if(i==0) d=x+y;
                else if(i==1) d=x-y;
                else if(i==2) d=-x+y;
                else d=-x-y;
                mp[d]=j;
            }
            int v1=mp.begin()->first,v2=mp.rbegin()->first;
            if(v2-v1>mx2){
                mx2=v2-v1;
            }
        }
        return min(mx1,mx2);
    }
