#include <bits/stdc++.h>
using namespace std;

#define ll long long; 


class Solution {
public:
    int minimumDistance(vector<vector<int>>& pnts)
    {
        int n=pnts.size();
        vector<int>sm(n,0);    
        vector<int>dif(n,0);
        int i=0;
        for (auto it:pnts)
        {
            sm[i]=it[0]+it[1];
            dif[i]=(it[0]-it[1]);
            i++;
        }
        sort(sm.begin(),sm.end());
        sort(dif.begin(),dif.end());
        // int rm1=-1;
        // int rm2=-1;
        // for (int i = 0; i < n; i++)
        // {
        //     cout<<sm[i]<<" "; 
        //     int sm1=pnts[i][0]+pnts[i][1];
        //     if(sm1==sm[n-1]){rm1=i;}
        //     if(sm1==sm[0]){rm2=i;}   
        // }
        // cout<<endl;
        // for(int i=0;i<n;i++)
        // {
        //     cout<<dif[i]<<" ";
        // }
        // cout<<endl;

        int ans1=INT_MAX,ans2=INT_MAX;
        ans1=min({sm[n-1]-sm[1],sm[n-2]-sm[0]});
        ans2=min({dif[n-1]-dif[1],dif[n-2]-dif[0]});
        // cout<<ans1<<" "<<ans2<<endl;
        int ans3=max({ans1,dif[n-1]-dif[0]});
        int ans4=max({ans2,sm[n-1]-sm[0]});
        int ans=min(ans3,ans4);
        for (int i = 0; i < n; i++)
        {
            int sm1=pnts[i][0]+pnts[i][1];
            int df1=pnts[i][0]-pnts[i][1];
            if(sm1==sm[n-1] and df1==dif[n-1]){ans=min(ans,max({sm[n-2]-sm[0],dif[n-2]-dif[0]}));}
            if(sm1==sm[n-1] and df1==dif[0]){ans=min(ans,max({sm[n-2]-sm[0],dif[n-1]-dif[1]}));}
            if(sm1==sm[0] and df1==dif[n-1]){ans=min(ans,max({sm[n-1]-sm[1],dif[n-2]-dif[0]}));}
            if(sm1==sm[0] and df1==dif[0]){ans=min(ans,max({sm[n-1]-sm[1],dif[n-1]-dif[1]}));}
        }

        return ans;
    }
};
