#define N 100010
#define x first
#define y second

/*
 (x[i] + y[i]) - (x[j] + y[j])
-(x[i] - y[i]) + (x[j] - y[j])
 (x[i] - y[i]) - (x[j] - y[j])
-(x[i] + y[i]) + (x[j] + y[j])

前后缀分解求前后缀的和和差的最大最小
然后枚举删哪个点即可
*/

typedef pair<int, int> PII;

const int INF = 1e9;

int add[N], sub[N];
int premaxadd[N], preminadd[N], sufmaxadd[N], sufminadd[N];
int premaxsub[N], preminsub[N], sufmaxsub[N], sufminsub[N];

class Solution {
public:
    int minimumDistance(vector<vector<int>>& p) {
        int n = p.size();
        for (int i = 0; i < n; i++) {
            add[i + 1] = p[i][0] + p[i][1];
            sub[i + 1] = p[i][0] - p[i][1];
        }

        premaxadd[0] = -INF;
        preminadd[0] = INF;
        sufmaxadd[n + 1] = -INF;
        sufminadd[n + 1] = INF;
        premaxsub[0] = -INF;
        preminsub[0] = INF;
        sufmaxsub[n + 1] = -INF;
        sufminsub[n + 1] = INF;
        for (int i = 1; i <= n; i++) {
            premaxadd[i] = max(premaxadd[i - 1], add[i]);
            preminadd[i] = min(preminadd[i - 1], add[i]);
            premaxsub[i] = max(premaxsub[i - 1], sub[i]);
            preminsub[i] = min(preminsub[i - 1], sub[i]);
        }

        for (int i = n; i > 0; i--) {
            sufmaxadd[i] = max(sufmaxadd[i + 1], add[i]);
            sufminadd[i] = min(sufminadd[i + 1], add[i]);
            sufmaxsub[i] = max(sufmaxsub[i + 1], sub[i]);
            sufminsub[i] = min(sufminsub[i + 1], sub[i]);
        }

        int res = INF;
        for (int i = 1; i <= n; i++) {
            int mxadd = max(premaxadd[i - 1], sufmaxadd[i + 1]);
            int mnadd = min(preminadd[i - 1], sufminadd[i + 1]);
            int mxsub = max(premaxsub[i - 1], sufmaxsub[i + 1]);
            int mnsub = min(preminsub[i - 1], sufminsub[i + 1]);
            res = min(res, max({
                mxadd - mnadd,
                -mnsub + mxsub,
                mxsub - mnsub,
                -mnadd + mxadd
            }));
        }
        return res;
    }
};
