class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        int n = points.size();
        int ans = INT_MAX;
        set<pair<int, int>> sx, sy;
        vector<int> x(n), y(n);
        for (int i = 0; i < n; i++) {
            x[i] = points[i][0] + points[i][1];
            y[i] = points[i][0] - points[i][1];
            sx.insert({x[i], i});
            sy.insert({y[i], i});
        }
        for (int i = 0; i < n; i++) {
            sx.erase({x[i], i});
            sy.erase({y[i], i});
            int curr = max(abs((*sy.rbegin()).first - (*sy.begin()).first), abs((*sx.rbegin()).first - (*sx.begin()).first));
            ans = min(ans, curr);
            sx.insert({x[i], i});
            sy.insert({y[i], i});
        }
        return ans;
    }
};
