func minimumDistance(p [][]int) int {
    n:=len(p)
    xx:=make([]int,n)
    yy:=make([]int,n)
    for i:=0;i<n;i++ {
        xx[i]=p[i][0]+p[i][1]
        yy[i]=p[i][0]-p[i][1]
    }

    // fmt.Println(xx)
    // fmt.Println(yy)

    xi:=make([]int,n)
    yi:=make([]int,n)
    for i:=0;i<n;i++ {
        xi[i]=i
        yi[i]=i
    }

    sort.Slice(xi,func(i,j int)bool {
        return xx[xi[i]]<xx[xi[j]]
    })

    sort.Slice(yi,func(i,j int)bool {
        return yy[yi[i]]<yy[yi[j]]
    })

    // fmt.Println(xi)
    // fmt.Println(yi)

    helper:=func(idx int) int {
        var mny,mxy,mnx,mxx int
        if yi[0]==idx {
            mny = yy[yi[1]]
        }else {
            mny = yy[yi[0]]
        }

        if xi[0]==idx {
            mnx = xx[xi[1]]
        }else {
            mnx = xx[xi[0]]
        }

        if yi[n-1]==idx {
            mxy = yy[yi[n-2]]
        } else {
            mxy = yy[yi[n-1]]
        }

        if xi[n-1]==idx {
            mxx = xx[xi[n-2]]
        } else {
            mxx = xx[xi[n-1]]
        }

        ans:=max(mxy-mny,mxx-mnx)
        // fmt.Println(mny,mxy,mnx,mxx,ans)

        return ans
    }

    ans:=1_000_000_000
    ans = min(ans,helper(yi[0]))
    ans = min(ans,helper(yi[n-1]))
    ans = min(ans,helper(xi[0]))
    ans = min(ans,helper(xi[n-1]))
    return ans
}
