using ll = long long;
using pii = pair<int, int>;
using vi = vector<int>;
using vvi = vector<vi>;

class Solution {
public:
	int minimumDistance(vector<vector<int>>& points) {
		vector<pii> as;
		for (auto &vec : points) {
			as.emplace_back(vec[0], vec[1]);
		}
		int n = sz(as);
		vector<int> inds(n); iota(all(inds), 0);
		vector<int> bs;
		auto get = [&](auto cmp) {
			nth_element(inds.begin(), inds.begin() + 2, inds.end(), cmp);
			bs.insert(bs.end(), inds.begin(), inds.begin() + 3);
		};
		get([&](int a, int b) { return as[a].first + as[a].second < as[b].first + as[b].second; });
		get([&](int a, int b) { return as[a].first + as[a].second > as[b].first + as[b].second; });
		get([&](int a, int b) { return as[a].first - as[a].second < as[b].first - as[b].second; });
		get([&](int a, int b) { return as[a].first - as[a].second > as[b].first - as[b].second; });
		sort(all(bs)); bs.erase(unique(all(bs)), bs.end());

		int ans = 1e9;
		auto solve = [&](int ban) {
			int res = 0;
			for (auto i : bs) for (auto j : bs) {
				if (i != ban && j != ban) {
					chmax(res, abs(as[i].first - as[j].first) + abs(as[i].second - as[j].second));
				}
			}
			chmin(ans, res);
		};
		for (auto ban : bs) solve(ban);
		return ans;
	}
};
