class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        multiset <int> dx,dy;
        for(auto &p:points){
            int x = p[0] , y = p[1];
            dx.insert(x+y);
            dy.insert(x-y);
        }
        int len  = 1e9;
        for(auto &p:points){
            int x = p[0] , y = p[1];
            int p1 = x+ y;
            int p2 = x - y;
            dx.erase(dx.find(p1));
            dy.erase(dy.find(p2));
            len = min(len,max(*dx.rbegin()-*dx.begin(),*dy.rbegin()-*dy.begin()));
            dx.insert(p1);
            dy.insert(p2);
        }
        return len;

    }
};
