class Solution {
public:
    int minimumDistance(vector<vector<int>>& v) {
        set<int> a,b;
        map<int,int> m1,m2;
        int n=v.size();
        for(int i=0; i<n; i++){
            int x=v[i][0]+v[i][1];
            int y=v[i][0]-v[i][1];
            a.insert(x);
            b.insert(y);
            m1[x]++;
            m2[y]++;
        }
        int ans=1e9;
        for(int i=0; i<n; i++){
            int x=v[i][0]+v[i][1];
            int y=v[i][0]-v[i][1];
            if(m1[x]==1)a.erase(a.find(x));
            if(m2[y]==1)b.erase(b.find(y));
            auto l1=a.begin();
            auto r1=b.begin();
            auto l2=a.end();
            l2--;
            auto r2=b.end();
            r2--;
            ans=min(ans, max(*l2-*l1, *r2-*r1));
            a.insert(x);
            b.insert(y);
        }
        return ans;
    }
};
