class Solution {
public:
    int minimumDistance(vector<vector<int>>& p) {
        int n = p.size();
        vector<int> v(n);
        vector<int> v1(n);
        int minDiffIndex = -1;
        int minDiff = INT_MAX;
        int maxDiffIndex = -1;
        int maxDiff = INT_MIN;
        int minSumIndex = -1;
        int minSum = INT_MAX;
        int maxSumIndex = -1;
        int maxSum = INT_MIN;
        for(int i = 0;i<n;i++){
            int h = p[i][0]-p[i][1];
            if(minDiff>h){
                minDiffIndex = i;
                minDiff = h;
            }
            if(maxDiff<h){
                maxDiff = h;
                maxDiffIndex = i;
            }

            int g = p[i][0]+p[i][1];
            if(minSum>g){
                minSumIndex = i;
                minSum = g;
            }
            if(maxSum<g){
                maxSum = g;
                maxSumIndex = i;
            }
        }
        vector<int> a(n-1,0);
        vector<int> b(n-1,0);

        int j = 0;
        for(int i = 0;i<n;i++){
            if(i==minDiffIndex){
                continue;
            }
            a[j] = p[i][0]+p[i][1];
            b[j] = p[i][0]-p[i][1];
            j++;
        }
        sort(a.begin(),a.end());
        sort(b.begin(),b.end());
        int max1 = max(a.back()-a.front(),b.back()-b.front());

        j = 0;
        for(int i = 0;i<n;i++){
            if(i==maxDiffIndex){
                continue;
            }
            a[j] = p[i][0]+p[i][1];
            b[j] = p[i][0]-p[i][1];
            j++;
        }
        sort(a.begin(),a.end());
        sort(b.begin(),b.end());

        max1 = min(max1,max(a.back()-a.front(),b.back()-b.front()));
        j = 0;
        for(int i = 0;i<n;i++){
            if(i==maxSumIndex){
                continue;
            }
            a[j] = p[i][0]+p[i][1];
            b[j] = p[i][0]-p[i][1];
            j++;
        }
        sort(a.begin(),a.end());
        sort(b.begin(),b.end());

        max1 = min(max1,max(a.back()-a.front(),b.back()-b.front()));

        j = 0;
        // cout<<minSumIndex<<endl;
        for(int i = 0;i<n;i++){
            if(i==minSumIndex){
                continue;
            }
            a[j] = p[i][0]+p[i][1];
            b[j] = p[i][0]-p[i][1];
            j++;
        }
        sort(a.begin(),a.end());
        sort(b.begin(),b.end());

        max1 = min(max1,max(a.back()-a.front(),b.back()-b.front()));

        return max1;







    }
};
