class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        vector<pair<int, int>> a, b;
        int n = points.size();
        for(int i = 0; i < points.size(); i++){
            a.push_back({points[i][0]+points[i][1], i});
            b.push_back({points[i][0]-points[i][1], i});
        }

        sort(a.begin(), a.end());
        sort(b.begin(), b.end());

        int mini = 1e9;
        for(int i = 0; i < points.size(); i++){
            int jx = 0, jy = n-1, kx = 0, ky = n-1;
            if(a.back().second == i) jy--;
            if(a.front().second == i) jx++;
            if(b.back().second == i) ky--;
            if(b.front().second == i) kx++;

            mini = min(mini, max(a[jy].first - a[jx].first, b[ky].first - b[kx].first));
        }
        return mini;
    }
};
