class Solution {
public:
    int minimumDistance(vector<vector<int>>& p) {
        int n=p.size();

        int minsum, maxsum, mindiff, maxdiff;
        minsum = maxsum = p[0][0] + p[0][1];
        mindiff = maxdiff = p[0][0] - p[0][1];
        int pt1=0;
        int pt2=0;
        int pt3=0;
        int pt4=0;
        for (int i = 0; i < n; i++) {
            int sum = p[i][0] + p[i][1];
            int diff = p[i][0] - p[i][1];
            if (sum < minsum){
                minsum = sum;
                pt1=i;
            }
            else if (sum > maxsum){
                maxsum = sum;
                pt2=i;
            }
            if (diff < mindiff){
                mindiff = diff;
                pt3=i;
            }
            else if (diff > maxdiff){
                maxdiff = diff;
                pt4=i;
            }
        }
        int maxi = max(maxsum - minsum, maxdiff - mindiff);
        vector<int> ans(n,0);
        for(int i=0 ; i<n ; i++){
            if(i==pt1 || i==pt2 || i==pt3 || i==pt4){
                continue;
            }
            ans[i]=maxi;
        }

        //remove pt1;
        if(pt1!=0){
            minsum = maxsum = p[0][0] + p[0][1];
            mindiff = maxdiff = p[0][0] - p[0][1];
        }
        else{
            minsum = maxsum = p[1][0] + p[1][1];
            mindiff = maxdiff = p[1][0] - p[1][1];
        }
        for (int i = 0; i < n; i++) {
            if(i==pt1){
                continue;
            }
            int sum = p[i][0] + p[i][1];
            int diff = p[i][0] - p[i][1];
            if (sum < minsum){
                minsum = sum;
                // pt1=i;
            }
            else if (sum > maxsum){
                maxsum = sum;
                // pt2=i;
            }
            if (diff < mindiff){
                mindiff = diff;
                // pt3=i;
            }
            else if (diff > maxdiff){
                maxdiff = diff;
                // pt4=i;
            }
        }
        ans[pt1]=max(ans[pt1],max(maxsum - minsum, maxdiff - mindiff));


        //remove pt2;
        if(pt2!=0){
            minsum = maxsum = p[0][0] + p[0][1];
            mindiff = maxdiff = p[0][0] - p[0][1];
        }
        else{
            minsum = maxsum = p[1][0] + p[1][1];
            mindiff = maxdiff = p[1][0] - p[1][1];
        }
        for (int i = 0; i < n; i++) {
            if(i==pt2){
                continue;
            }
            int sum = p[i][0] + p[i][1];
            int diff = p[i][0] - p[i][1];
            if (sum < minsum){
                minsum = sum;
                // pt1=i;
            }
            else if (sum > maxsum){
                maxsum = sum;
                // pt2=i;
            }
            if (diff < mindiff){
                mindiff = diff;
                // pt3=i;
            }
            else if (diff > maxdiff){
                maxdiff = diff;
                // pt4=i;
            }
        }
        ans[pt2]=max(ans[pt2],max(maxsum - minsum, maxdiff - mindiff));

        //remove pt3;
        if(pt3!=0){
            minsum = maxsum = p[0][0] + p[0][1];
            mindiff = maxdiff = p[0][0] - p[0][1];
        }
        else{
            minsum = maxsum = p[1][0] + p[1][1];
            mindiff = maxdiff = p[1][0] - p[1][1];
        }
        for (int i = 0; i < n; i++) {
            if(i==pt3){
                continue;
            }
            int sum = p[i][0] + p[i][1];
            int diff = p[i][0] - p[i][1];
            if (sum < minsum){
                minsum = sum;
                // pt1=i;
            }
            else if (sum > maxsum){
                maxsum = sum;
                // pt2=i;
            }
            if (diff < mindiff){
                mindiff = diff;
                // pt3=i;
            }
            else if (diff > maxdiff){
                maxdiff = diff;
                // pt4=i;
            }
        }
        ans[pt3]=max(ans[pt3],max(maxsum - minsum, maxdiff - mindiff));


        //remove pt4;
        if(pt4!=0){
            minsum = maxsum = p[0][0] + p[0][1];
            mindiff = maxdiff = p[0][0] - p[0][1];
        }
        else{
            minsum = maxsum = p[1][0] + p[1][1];
            mindiff = maxdiff = p[1][0] - p[1][1];
        }
        for (int i = 0; i < n; i++) {
            if(i==pt4){
                continue;
            }
            int sum = p[i][0] + p[i][1];
            int diff = p[i][0] - p[i][1];
            if (sum < minsum){
                minsum = sum;
                // pt1=i;
            }
            else if (sum > maxsum){
                maxsum = sum;
                // pt2=i;
            }
            if (diff < mindiff){
                mindiff = diff;
                // pt3=i;
            }
            else if (diff > maxdiff){
                maxdiff = diff;
                // pt4=i;
            }
        }
        ans[pt4]=max(ans[pt4],max(maxsum - minsum, maxdiff - mindiff));


        int ret=1e9;
        // cout<<pt1<<" "<<pt2<<" "<<pt3<<" "<<pt4<<endl;
        for(auto e : ans){
            // cout<<e<<" ";
            ret=min(e,ret);
        }

        return ret;

    }
};
