class Solution {
public:
    int findMin(vector<vector<int> > & points){
        int n =  points.size();
        vector<pair<int,int> > sum,diff;
        for(int i=0;i<n;i++){
            sum.push_back(make_pair(points[i][0]+points[i][1],i));
        }
        for(int i=0;i<n;i++){
            diff.push_back(make_pair(points[i][0]-points[i][1],i));
        }
        sort(sum.begin(),sum.end());
        sort(diff.begin(),diff.end());
        int s=sum.size();
        int d=diff.size();
        return max(sum[s-1].first-sum[0].first,diff[d-1].first-diff[0].first);
    }
    int minimumDistance(vector<vector<int>>& points) {
        int n =  points.size();
        vector<pair<int,int> > sum,diff;
        for(int i=0;i<n;i++){
            sum.push_back(make_pair(points[i][0]+points[i][1],i));
        }
        for(int i=0;i<n;i++){
            diff.push_back(make_pair(points[i][0]-points[i][1],i));
        }
        sort(sum.begin(),sum.end());
        sort(diff.begin(),diff.end());
        int s=sum.size();
        int d=diff.size();
        int n1,n2;
        if(sum[s-1].first-sum[0].first>diff[d-1].first-diff[0].first){
            n1=sum[0].second;
            n2=sum[s-1].second;
        }else{
            n1=diff[0].second;
            n2=diff[d-1].second;
        }
        vector<vector<int> > p1,p2;
        for(int i=0;i<n;i++){
            if(i!=n1){
                p1.push_back(points[i]);
            }
            if(i!=n2){
                p2.push_back(points[i]);
            }
        }
        return min(findMin(p1),findMin(p2));
    }
};
