class Solution {
public:
    int minimumDistance(vector<vector<int>>& a) {
        int n=a.size(),i,ans=1e9,mx;
        multiset<int> b,c;
        for(i=0;i<n;i++)
        {
            b.insert(a[i][0]+a[i][1]);
            c.insert(a[i][0]-a[i][1]);
        }
        for(i=0;i<n;i++)
        {
            mx=0;
            auto it=b.find(a[i][0]+a[i][1]);
            b.erase(it);
            mx=max(mx,*(--b.end())-*(b.begin()));
            it=c.find(a[i][0]-a[i][1]);
            c.erase(it);
            mx=max(mx,*(--c.end())-*(c.begin()));
            b.insert(a[i][0]+a[i][1]);
            c.insert(a[i][0]-a[i][1]);
            ans=min(ans,mx);
        }
        return ans;
    }
};
