class Solution {
public:


    vector<int> get_candidates(vector<vector<int>>& p) {
        int min_1 = 0, max_1 = 0, min_2 = 0, max_2 = 0;

        for(int i=0; i<p.size(); i++) {

            if(p[i][0] + p[i][1] < p[min_1][0] + p[min_1][1])
                min_1 = i;

            if(p[i][0] + p[i][1] > p[max_1][0] + p[max_1][1])
                max_1 = i;

            if(p[i][0] - p[i][1] < p[min_2][0] - p[min_2][1])
                min_2 = i;

            if(p[i][0] - p[i][1] > p[max_2][0] - p[max_2][1])
                max_2 = i;

        }

        return {max_1, max_2, min_1, min_2};

    }

    int get_max(vector<vector<int>>& p) {


        int min_1 = 0, max_1 = 0, min_2 = 0, max_2 = 0;

        for(int i=0; i<p.size(); i++) {

            if(p[i][0] + p[i][1] < p[min_1][0] + p[min_1][1])
                min_1 = i;

            if(p[i][0] + p[i][1] > p[max_1][0] + p[max_1][1])
                max_1 = i;

            if(p[i][0] - p[i][1] < p[min_2][0] - p[min_2][1])
                min_2 = i;

            if(p[i][0] - p[i][1] > p[max_2][0] - p[max_2][1])
                max_2 = i;

        }

        int dist_1 = p[max_1][0] + p[max_1][1] - (p[min_1][0] + p[min_1][1]);
        int dist_2 = p[max_2][0] - p[max_2][1] - (p[min_2][0] - p[min_2][1]);

        return max(dist_1, dist_2);

    }


    int minimumDistance(vector<vector<int>>& points) {

        sort(begin(points), end(points));

        auto cand = get_candidates(points);

        int res = INT_MAX;

        for(int x : cand) {
            auto v = points;
            v.erase(begin(v) + x);
            res = min(res, get_max(v));
        }

        return res;

    }
};
