class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {

        multiset<int>addSet,subSet;

        for(auto point : points){

            int x= point[0];
            int y = point[1];

            addSet.insert(x+y);
            subSet.insert(x-y);
        }

        int mn = INT_MAX;
        for(auto point : points){

            int x= point[0];
            int y = point[1];

            addSet.erase(addSet.find(x+y));
            subSet.erase(subSet.find(x-y));

            int mx1 = (*addSet.rbegin())-(*addSet.begin());
            int mx2 = (*subSet.rbegin())-(*subSet.begin());


            addSet.insert(x+y);
            subSet.insert(x-y);

            int mx3 = max(mx1,mx2);

            mn = min(mn,mx3);


        }


        return mn;



    }
};
