class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        int n = points.size();
        vector<vector<int>> new_points;
        for(auto& p : points) 
            new_points.push_back({p[0]+p[1], p[0]-p[1]});

        // for(auto p:new_points)
        //     cout<<p[0]<<" "<<p[1]<<endl;
        // cout<<endl;

        sort(new_points.begin(), new_points.end());
        vector<vector<int>> temp;
        int ans1 = new_points[n-2][0] - new_points[0][0];
        temp.insert(temp.end(), new_points.begin(), new_points.end() - 1);
        sort(temp.begin(), temp.end(), [](vector<int>& a, vector<int>& b) {
            return a[1] < b[1];
        });
        ans1 = max(ans1, temp[n-2][1] - temp[0][1]);
        int ans2 = new_points[n-1][0] - new_points[1][0];
        temp.clear();
        temp.insert(temp.end(), new_points.begin() + 1, new_points.end());
        sort(temp.begin(), temp.end(), [](vector<int>& a, vector<int>& b) {
            return a[1] < b[1];
        });
        ans2 = max(ans2, temp[n-2][1] - temp[0][1]);
        // cout<<ans2<<endl;

        sort(new_points.begin(), new_points.end(), [](vector<int>& a, vector<int>& b) {
            return a[1] < b[1];
        });
        temp.clear();
        int ans3 = new_points[n-2][1] - new_points[0][1];
        temp.insert(temp.end(), new_points.begin(), new_points.end() - 1);
        sort(temp.begin(), temp.end());
        ans3 = max(ans3, temp[n-2][0] - temp[0][0]);
        int ans4 = new_points[n-1][1] - new_points[1][1];
        temp.clear();
        temp.insert(temp.end(), new_points.begin() + 1, new_points.end());
        sort(temp.begin(), temp.end());
        ans4 = max(ans4, temp[n-2][0] - temp[0][0]);

        return min(min(ans1, ans2), min(ans3, ans4));
    }
};
